create table remote_station (id bigint generated by default as identity, city varchar(255), code varchar(255), priority varchar(255), state varchar(255), primary key (id))
create table route (id bigint generated by default as identity, cost decimal(19,2), lead_time double, quantity integer, part_id bigint, remote_station_destination_id bigint, remote_station_origin_id bigint, primary key (id))
create table sram (id bigint generated by default as identity, material_available double, material_in_use double, volume double, capacity integer, code varchar(255), humidity double, temperature double, fixed_cost decimal(19,2), process_status varchar(255), status varchar(255), primary key (id))
create table srams_allocated (id bigint generated by default as identity, number_of_srams integer, remote_station_id bigint, primary key (id))
create table stock (id bigint generated by default as identity, capital_cost double, stockout_cost decimal(19,2), primary key (id))
